import React, { useEffect, useState } from "react";

const apiUrl = "http://localhost:3001/api/items";

function App() {
    const [items, setItems] = useState([]);
    const [form, setForm] = useState({ id: "", name: "", description: "" });

    useEffect(() => {
        fetchItems();
    }, []);

    function fetchItems() {
        fetch(apiUrl)
            .then(res => res.json())
            .then(setItems);
    }

    function handleChange(e) {
        const { id, value } = e.target;
        setForm(f => ({ ...f, [id.replace("item-", "")]: value }));
    }

    function handleSubmit(e) {
        e.preventDefault();
        const { id, name, description } = form;
        const method = id ? "PUT" : "POST";
        const url = id ? `${apiUrl}/${id}` : apiUrl;
        fetch(url, {
            method,
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({ name, description })
        }).then(() => {
            setForm({ id: "", name: "", description: "" });
            fetchItems();
        });
    }

    function handleEdit(item) {
        setForm({ id: item.id, name: item.name, description: item.description || "" });
    }

    function handleDelete(id) {
        fetch(`${apiUrl}/${id}`, { method: "DELETE" })
            .then(fetchItems);
    }

    return (
        <div>
            <h1>Items</h1>
            <form onSubmit={handleSubmit}>
                <input type="hidden" id="item-id" value={form.id} />
                <input
                    type="text"
                    id="item-name"
                    placeholder="Name"
                    required
                    value={form.name}
                    onChange={handleChange}
                />
                <input
                    type="text"
                    id="item-description"
                    placeholder="Description"
                    value={form.description}
                    onChange={handleChange}
                />
                <button type="submit">{form.id ? "Update" : "Add"} Item</button>
            </form>
            <ul>
                {items.map(item => (
                    <li key={item.id}>
                        {item.name}: {item.description || ""}
                        <button onClick={() => handleEdit(item)}>Edit</button>
                        <button onClick={() => handleDelete(item.id)}>Delete</button>
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default App;
